<?phpnamespace Ffhs\FilamentPackageFfhsCustomForms\Filament\Component\FormEditor\TypeActions;use Ffhs\FilamentPackageFfhsCustomForms\CustomFieldType\GenericType\CustomFieldType;use Ffhs\FilamentPackageFfhsCustomForms\Facades\CustomForms;use Ffhs\FilamentPackageFfhsCustomForms\Traits\HasFormConfiguration;use Filament\Actions\Action;class FieldTypeAction extends Action{    use HasFormConfiguration;    protected function setUp(): void    {        parent::setUp();        $this->iconButton();    }    protected function getFieldKey(): string    {        $path = $this->getSchemaComponent()->getStatePath();        $parts = explode('.', $path);        return end($parts);    }    protected function getFieldType(): CustomFieldType    {        $state = $this->getSchemaComponent()->getState();        return once(            fn() => CustomForms::getFieldTypeFromRawDate($state, $this->getFormConfiguration())        );    }    protected function resolveDefaultClosureDependencyForEvaluationByName(string $parameterName): array    {        return match ($parameterName) {            'value' => [$this->getFieldKey()],            'type' => [$this->getFieldType()],            'formConfiguration' => [$this->getFormConfiguration()],            default => parent::resolveDefaultClosureDependencyForEvaluationByName($parameterName),        };    }}